contract sureCoin  {

  constructor () public {
    bytes32 name = "sureCoin";
    bytes32 symbol = "SCO";
    uint decimals = 2;
    // million coins
    uint INITIAL_SUPPLY = 1000000 * (10 ** decimals);
    uint totalSupply = INITIAL_SUPPLY;
    balances[msg.sender] = INITIAL_SUPPLY;
  }

  mapping(address => uint) balances; // List of user balances.

  event Transfer(address indexed from, address indexed to, uint value);

  function transfer(address _to, uint _value) {
    balances[msg.sender] = balances[msg.sender] - _value;
    balances[_to] = balances[_to] + _value;

    emit Transfer(msg.sender, _to, _value);
  }
    
  function balanceOf(address _owner) constant returns (uint balance) {
    return balances[_owner];
  }
}

contract blockChainMutual {

  address public owner;
  
  // Constructor
  constructor () public {
    owner = msg.sender;
    // send my coins to this contract
    // do I even have them?
  }
  
  
  // model a policy holder
  struct policyholder {
    bool current;
    bool isExist;
  }

  // this array will store the policyholders, including the current flag
  mapping(address=> policyholder) public policyholders;
  
  //Store policyholder Count.. can't count it later in the blockchain
  uint public policyholdersCount;
  
  function buyPolicy () private {
    // check if policyholder exists

    // this function looks to be blowing everything up..
    // is there a way to make it a bit more efficient, perhaps by pushing a list. fewer operations, anyway
    
    if (policyholders[msg.sender].isExist) {
      /// ugh this is harder than I want it to be.. maybe I'll revert to the prior code and just

      //uint len = policyholders[msg.sender].length;
      require(policyholders[msg.sender].current == false, "you're already a policyholder!");
      
      // policyholder is not current, make them current!
      delete policyholders[msg.sender].current;
      policyholders[msg.sender].current=true;
      policyCount ++;
      //delete inForcePolicyholders[msg.sender];
      //inForcePolicyholders[msg.sender]=true;
    }
    else {
      // policyholder doesn't exist, add him/her
      addPolicyholder();
      policyCount ++;
      //inForcePolicyholders[msg.sender]=true;
    }
    emit policyEvent();
  }
  
  function cancelPolicy () private {
    
    policyholders[msg.sender].current=false;
    policyCount --;
    //delete inForcePolicyholders[msg.sender];
    emit policyEvent();
  }
  
  uint public policyCount;
  
  function addPolicyholder () private {
    policyholdersCount ++;
    policyholders[msg.sender] = policyholder(true, true);
    //inForcePolicyholders[msg.sender]=true;
  }

  
  // store accounts that have voted
  //mapping(address=> bool) public inForcePolicyholders;

  // record that we added or canceled a policy
  event policyEvent ();

  // self destruct
  function kill() public {
    if(msg.sender == owner) selfdestruct(owner);
  }

}
